import { useState } from "react"
import { InputCheckbox } from "../InputCheckbox"
import { TransactionPaneComponent } from "./types"
import mockData from "../../mock-data.json"
export const TransactionPane: TransactionPaneComponent = ({
  transaction,
  loading,
  setTransactionApproval
}) => {
  const [approved, setApproved] = useState(transaction.approved)
  const handleApprovalChange = (newValue: boolean) => {
    // Update the approval status in mockData directly
    const updatedTransactionIndex = mockData.transactions.findIndex(
      (t) => t.id === transaction.id
    )

    if (updatedTransactionIndex !== -1) {
      mockData.transactions[updatedTransactionIndex].approved = newValue
      setApproved(newValue)

      // If you still want to use setTransactionApproval for consistency:
      if (setTransactionApproval) {
        setTransactionApproval({
          transactionId: transaction.id,
          newValue: newValue,
        });
      }
    }
  }
  return (
    <div className="RampPane">
      <div className="RampPane--content">
        <p className="RampText">{transaction.merchant} </p>
        <b>{moneyFormatter.format(transaction.amount)}</b>
        <p className="RampText--hushed RampText--s">
          {transaction.employee.firstName} {transaction.employee.lastName} - {transaction.date}
        </p>
      </div>
      <InputCheckbox
        id={transaction.id}
        checked={approved}
        disabled={loading}
        onChange={handleApprovalChange}
      />
    </div>
  )
}

const moneyFormatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
})
